mapper.insert:

insert into base_config (ID, CONFIG_CODE, CONFIG_REMARK, 
  CONFIG_VALUE_FILE_KEY, CONFIG_VALUE, CREATION_TIME, 
  CREATION_USER_ID, LAST_UPDATE_TIME, LAST_UPDATE_USER_ID, 
  DELETE_TIME, DELETE_USER_ID, MARK_DELETE
  )
values (#{id,jdbcType=VARCHAR}, #{configCode,jdbcType=VARCHAR}, #{configRemark,jdbcType=VARCHAR}, 
  #{configValueFileKey,jdbcType=VARCHAR}, #{configValue,jdbcType=VARCHAR}, #{creationTime,jdbcType=TIMESTAMP}, 
  #{creationUserId,jdbcType=VARCHAR}, #{lastUpdateTime,jdbcType=TIMESTAMP}, #{lastUpdateUserId,jdbcType=VARCHAR}, 
  #{deleteTime,jdbcType=TIMESTAMP}, #{deleteUserId,jdbcType=VARCHAR}, #{markDelete,jdbcType=INTEGER}
  )
